SELECT distinct C.ID id, C.NAME name, C.TITLE title, C.STATUS status, C.LEVEL level, C.DISPLAY_ORDER displayOrder, C.URL url, C.CODE code, C.CLASS_NAME className, C.ICON icon, C.PARENT_ID parentId, C.CREATED_TIME createdTime, C.CREATED_OPERATOR createdOperator, C.LAST_DATE lastDate, C.LAST_OPERATOR lastOperator, C.ABSTRACT_LEVEL abstractLevel, C.REMARK remark FROM TF_ROLE_MENU A LEFT JOIN TF_USER_ROLE B ON B.ROLE_ID = A.ROLE_ID LEFT JOIN TF_MENU C ON C.ID = A.MENU_ID
B.USER_ID = #{userId,jdbcType=NUMERIC}
AND LEVEL = #{level}
ORDER BY DISPLAY_ORDER

SELECT distinct C.ID id, C.NAME name, C.TITLE title, C.STATUS status, C.LEVEL level, C.DISPLAY_ORDER displayOrder, C.URL url, C.CODE code, C.CLASS_NAME className, C.ICON icon, C.PARENT_ID parentId, C.CREATED_TIME createdTime, C.CREATED_OPERATOR createdOperator, C.LAST_DATE lastDate, C.LAST_OPERATOR lastOperator, C.ABSTRACT_LEVEL abstractLevel, C.REMARK remark FROM TF_ROLE_MENU A LEFT JOIN TF_USER_ROLE B ON B.ROLE_ID = A.ROLE_ID LEFT JOIN TF_MENU C ON C.ID = A.MENU_ID
B.USER_ID = #{userId}
AND C.PARENT_ID = #{parentId}
AND C.STATUS = #{status}
ORDER BY DISPLAY_ORDER

SELECT ur.ID id, ur.USER_ID userId, su.NICK_NAME nickName, su.LOGIN_NAME loginName, ur.ROLE_ID roleId, TR.NAME roleName, ur.AREA_CODE areaCode, ur.STATUS status, ur.CODE code, ur.LIMIT_PURVIEW limitPurview, ur.LIMIT_CODE limitCode, ur.START_DATE startDate, ur.END_DATE endDate, ur.REMARK remark FROM TF_USER_ROLE ur LEFT JOIN TF_SYSTEM_USER SU ON SU.ID = UR.USER_ID LEFT JOIN TF_ROLE TR ON TR.ID = UR.ROLE_ID
USER_ID = #{userId}
ROLE_ID = #{roleId}
AND STATUS = #{status}
ORDER BY ID

SELECT ID id, NAME name, TITLE title, STATUS status, DISPLAY_ORDER displayOrder, CODE code, PARENT_ID parentId, CREATED_TIME createdTime, CREATED_OPERATOR createdOperator, LAST_DATE lastDate, LAST_OPERATOR lastOperator, REMARK remark FROM TF_ROLE
AND NAME = #{name}
ORDER BY DISPLAY_ORDER

SELECT STU.ID id, STU.LOGIN_NAME loginName, STU.PASSWORD password, STU.AREA_CODE areaCode, TA.NAME areaName, STU.NICK_NAME nickName, STU.SERVICE_SHOP_CODE serviceShopCode, TSI.NAME shopName, STU.REMARK remark, STU.TYPE type, STU.PHONE_NUMBER phoneNumber, STU.USER_MODE userMode, STU.OPEN_IM openIm, STU.CREATE_DATE createDate, STU.VERSION version FROM TF_SYSTEM_USER STU LEFT JOIN TF_SHOP_INFO TSI ON TSI.SHOP_CODE = STU.SERVICE_SHOP_CODE LEFT JOIN TF_AREA_INFO TA ON TA.CODE = STU. AREA_CODE
NICK_NAME = #{nickName}
ORDER BY ID

